import socket
from processing_package import package_data_processing
from threading import Thread
from work_db import Packet_data

import mysql.connector
from config import host, user, password, db_name


# Функция подключения к бд.
def connect_db():
    try:
        # Подключаемся к бд.
        db_connection = mysql.connector.connect(
            host=host,
            user=user,
            password=password,
            database=db_name
        )

        return db_connection

    except Exception as e:
        print(e)


# Процедура получения и отправки пакетов.
def receive_data(connection, data_for_db, db_connection):
    try:
        while True:
            # Получаем данные от клиента
            data = connection.recv(1024)

            if not data:
                break

            print("Получены данные:", data)
            connection.send(package_data_processing(data, data_for_db))

    except KeyboardInterrupt:
        pass

    # Закрываем соединение с бд.
    db_connection.close()
    # Закрываем соединение.
    connection.close()


# Процедура работы сервера.
def server_work():
    # Создаем подключение к бд.
    db_connection = connect_db()

    port = 1337

    # Создаем сокет для прослушивания порта
    server_socket = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
    server_socket.bind(('', port))
    server_socket.listen(1)

    print("Сервер запущен и слушает порт {}...".format(port))

    try:
        while True:
            # Принимаем входящее соединение
            connection, address = server_socket.accept()
            print("Установлено соединение с {}".format(address))

            # Создаем объект класса, для последующей записи данных в бд.
            data_for_db = Packet_data()
            data_for_db.create_cursor(db_connection)

            # Создаем новый поток для обработки данных клиента
            t = Thread(target=receive_data, args=(connection, data_for_db, db_connection))
            t.start()

    except KeyboardInterrupt:
        pass

    # Закрываем сокет.
    server_socket.close()


